sources.scss\\partials\\_base.scss:
body {
    color: $gray;
    font-family: $ff-s;
    line-height: 1.6;
    background-color: $white;
    font-size: .875rem;
    overflow-x: hidden;
    @include mq {
        font-size: 1.125rem;
    }
}

img {
    width: 100%;
}

h1, h2 ,p {
    margin-top: 0;
    margin-bottom: 1em;
}

h1 {
    color: $accent;
    line-height: 1;
    font-size: 3.125rem;

    @include mq {
        font-size: 4rem;
    }
}

h2 {
    @include sans-serif($fs: 2rem);

    @include mq {
        font-size: 3rem;
    }
}

sources.scss\\modulers\\_mixins.scss:
@mixin container($width: 90%, $max-width: 50em) {
    width: $width;
    margin: 0 auto;
    max-width: $max-width;
}

@mixin mq($width: 50em) {
    @media (min-width: $width) {
        @content;
    }
}

sources.scss\\partials\\_nav.scss:
.menu-button {
    position: absolute;
    top: 3em;
    left: 3em;
    background: transparent;
    border: 0;
    cursor: pointer;
    outline: none;
    z-index: 5;
}

nav {
    position: fixed;
    top: 0;
    bottom: 0;
    left: 0;
    right: 15%;
    z-index: 10;

    transform: translateX(-100%);
    transition: transform ease-out 250ms;
}

nav ul {
    margin: 0;
    padding: 0;
    list-style: none;
    display: flex;
    flex-direction: column;
    height: 100vh;
    justify-content: space-around;
    align-items: stretch;
    background: $accent;
}

nav li{
    text-align: center;
    &:not(:last-of-type) {
        border-bottom: 1px solid $dark-blue;
    }
}

nav li:hover {
    background-color: rgba($color: $dark-blue, $alpha: .5);
}

nav a {
    @include sans-serif($fs: 4rem, $color: $dark-blue);
    text-decoration: none;
    padding: calc(100vh / 8) 0;
    line-height: 0;
    // background-color: lightpink;
    display: inline-block;
    width: 100%;
}

.close {
    display: none;
}

.nav-is-open {
    height: 100vh;
    overflow-y: hidden ;

    nav {
        transform: translateX(0);
    }

    .container-all {
        transform: translateX(85%);
    }

    .menu-icon-img {
        display: none;
    }

    .close {
        display: block;
    }

    .menu-button {
        transform: translateX(-75%);
    }
}

.container-all {
    transition: transform ease-out 250ms;
}

sources.scss\\partials\\_welcome.scss:
.welcome {
    background-image: url(../img/header-bg.png);
    background-size: cover;
    background-position: center;
    height: 100vh;
    display: flex;
    justify-content: center;
    align-items: center;
    position: relative;

    .menu-icon-img {
        height: 17px;
        width: 30px;
    }

    .close {
        height: 17px;
        width: 17px;
    }

    h1{
        font-size: 1.5rem;
        font-weight: $fw-n;
        color: $dark-blue;
        line-height: 2;
        text-align: center;

        @include mq {
            font-size: 3vw;
        }

        span {
            @include sans-serif($fs: 5.5rem, $color: $accent);
            display: block;

            @include mq {
                font-size: 10vw;
            }
        }

        &::before,
        &::after{
            content: "";
            display: block;
            width: 60%;
            height: 1px;
            background: $dark-blue;
        }
        &::before{
            margin: 0 auto 2em;
        }
        &::after{
            margin: 2em auto 0;
        }
    }
}

